        // 最初の1週だけ見る
        for (int i = 0; i <= d; ++i) {
            if (s <= t) {
                ans = i;
                break;
            }
            if (i < d) s += w[i];
        }
    } else {
        lint ok = 100001, ng = -1;
        while (ok - ng > 1) {
            lint mid = (ok + ng) / 2;
            lint ss = s + mid * wsum;

            bool judge = false;
            for (int i = 0; i <= d; ++i) {
                if (ss <= t) {
                    ans = min(ans, mid * d + i);
                    judge = true;
                }
                if (i < d) ss += w[i];
            }

            (judge ? ok : ng) = mid;
        }
    }

    cout << (ans == INF ? -1 : ans) << endl;
    return 0;
}

