#define MAX 100101
ll S,T,D,w[MAX];

ll fin(ll s,ll t) {
  rep(i,D) {
    s += w[i];
    if( s <= t ) return i + 1;
  }
  return -1;
}

bool check(ll x, ll delta, ll delta_max) {
  assert( x );
  return S + x * delta > T && S + ( x - 1 ) * delta + delta_max > T ;
}

ll bf() {
  ll cnt = 0;
  ll s = S, t = T;
  int  i = 0;
  while( s > t ) {
    s += w[i%D];
    ++cnt;
    ++i;
  }
  return cnt;
}

void compute() {
  {
    assert( S > T );
    ll tmp = fin(S,T);
    if( tmp != -1 ) {
      cout << tmp << endl;
      return;
    }
  }
  //cout << bf() << endl;
  ll delta = 0, delta_max = 0;
  rep(i,D) {
    delta += w[i];
    delta_max = min(delta_max,delta);
  }
  if( debug ) {
    cout << "delta = " << delta << ", delta_max = " << delta_max << endl;
  }

  if( delta >= 0LL ) { puts("-1"); return; }
  ll L = 0, R = 1000000LL;
  while( R - L ){
    ll M = ( L + R ) / 2LL;
    if( check(M,delta,delta_max) ) L = M + 1LL;
    else                 R = M;  
  }
  ll x = L - 1LL;
  if( debug ) {
    cout << "x = " << x << endl;
  }
  assert( x > 0LL );
  ll ans = x * D + fin( S + x * delta, T );

  //assert( bf() == ans );
  cout << ans << endl;
}

