                if (components > maxComponents) {
                    maxComponents = components;
                }
            }
        }
        System.out.println(maxComponents);
    }

    private static List<Integer> findPath(int current, int target, int parent, List<Integer> path) {
        path.add(current);
        if (current == target) {
            return new ArrayList<>(path);
        }
        for (int neighbor : tree.get(current)) {
            if (neighbor != parent) {
                List<Integer> result = findPath(neighbor, target, current, path);
                if (result != null) {
                    return result;
                }
            }
        }
        path.remove(path.size() - 1);
        return null;
    }

    private static int countValidComponents(boolean[] removed) {
        boolean[] visited = new boolean[removed.length];
        int count = 0;
        for (int i = 1; i < removed.length; i++) {
            if (!removed[i] && !visited[i]) {
                int size = dfs(i, removed, visited);
                if (size >= K) {
                    count++;
                }
            }
        }
        return count;
    }

    private static int dfs(int node, boolean[] removed, boolean[] visited) {
        visited[node] = true;
        int size = 1;
        for (int neighbor : tree.get(node)) {
            if (!removed[neighbor] && !visited[neighbor]) {
                size += dfs(neighbor, removed, visited);
            }
        }
        return size;
    }
}

