public class TreeComponentMaximizer {
    private List<List<Integer>> graph;
    private int n;
    private int k;
    private int[] subtreeSize;

    public int maximizeComponents(int n, List<List<Integer>> edges, int k) {
        this.n = n;
        this.k = k;
        graph = new ArrayList<>(n);
        for (int i = 0; i < n; i++) {
            graph.add(new ArrayList<>());
        }

        for (List<Integer> edge : edges) {
            int u = edge.get(0), v = edge.get(1);
            graph.get(u).add(v);
            graph.get(v).add(u);
        }

        subtreeSize = new int[n];
        calculateSubtreeSizes(0, -1);

        int maxComponents = 0;
        for (int u = 0; u < n; u++) {
            for (int v = u + 1; v < n; v++) {
                maxComponents = Math.max(maxComponents, countComponentsAfterRemoval(u, v));
            }
        }

        return maxComponents;
    }

    private void calculateSubtreeSizes(int node, int parent) {
        subtreeSize[node] = 1;
        for (int child : graph.get(node)) {
            if (child != parent) {
                calculateSubtreeSizes(child, node);
                subtreeSize[node] += subtreeSize[child];
            }
        }
    }

    private int countComponentsAfterRemoval(int u, int v) {
        List<Integer> path = findPath(u, v);
        Set<Integer> pathSet = new HashSet<>(path);

        boolean[] visited = new boolean[n];
        int components = 0;

        for (int i = 0; i < n; i++) {
            if (!visited[i] && !pathSet.contains(i)) {
                if (dfs(i, pathSet, visited) >= k) {
                    components++;
                }
            }
        }

        return components;
    }

    private List<Integer> findPath(int u, int v) {
        List<Integer> path = new ArrayList<>();
        int[] parent = new int[n];
        Arrays.fill(parent, -1);

        Queue<Integer> queue = new LinkedList<>();
        queue.offer(u);
        parent[u] = u;

        while (!queue.isEmpty()) {
            int current = queue.poll();
            if (current == v) break;

            for (int neighbor : graph.get(current)) {
                if (parent[neighbor] == -1) {
                    parent[neighbor] = current;
                    queue.offer(neighbor);
                }
            }
        }

        int current = v;
        while (current != u) {
            path.add(current);
            current = parent[current];
        }
        path.add(u);
        Collections.reverse(path);

        return path;
    }

    private int dfs(int node, Set<Integer> pathSet, boolean[] visited) {
        visited[node] = true;
        int size = 1;

        for (int neighbor : graph.get(node)) {
            if (!visited[neighbor] && !pathSet.contains(neighbor)) {
                size += dfs(neighbor, pathSet, visited);
            }
        }

        return size;
    }
}

