        for v in self.graph[u]:
            if v == parent:
                continue
            self.size[u] += self.dfs_size(v, u)
        return self.size[u]

    def max_k_components(self, K):
        max_components = 0
        for u in range(self.n):
            for v in self.graph[u]:
                if u < v:
                    component_size = self.n - self.size[u] - self.size[v]
                    if component_size >= K:
                        continue

                    acc_size = self.dfs_acc_component_size(v, u, K)
                    components = acc_size // K + (component_size >= K)
                    max_components = max(max_components, components)
        return max_components

    def dfs_acc_component_size(self, u, parent, K):
        acc_size = 0
        for v in self.graph[u]:
            if v == parent:
                continue
            sub_size = self.dfs_acc_component_size(v, u, K)
            if sub_size >= K:
                acc_size += sub_size
            else:
                acc_size += self.size[v]
        return acc_size


