def solve(n, edges, k):
    # Build adjacency list representation of the tree
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)

    def find_path(u, v):
        # Find simple path between u and v using BFS
        parent = {u: None}
        queue = deque([u])

        while queue:
            curr = queue.popleft()
            if curr == v:
                break

            for neighbor in graph[curr]:
                if neighbor not in parent:
                    parent[neighbor] = curr
                    queue.append(neighbor)

        # Reconstruct path
        path = []
        curr = v
        while curr is not None:
            path.append(curr)
            curr = parent[curr]

        return list(reversed(path))

    def count_components(removed_vertices):
        # Build new graph without removed vertices and their adjacent edges
        new_graph = defaultdict(list)
        for u in range(1, n+1):
            if u not in removed_vertices:
                for v in graph[u]:
                    if v not in removed_vertices:
                        new_graph[u].append(v)

        # Count number of components with K or more vertices
        visited = set()
        component_count = 0

        for u in range(1, n+1):
            if u not in removed_vertices and u not in visited:
                component = set()
                queue = deque([u])

                while queue:
                    curr = queue.popleft()
                    if curr in visited:
                        continue

                    visited.add(curr)
                    component.add(curr)

                    for neighbor in new_graph[curr]:
                        if neighbor not in visited:
                            queue.append(neighbor)

                if len(component) >= k:
                    component_count += 1

        return component_count

    max_components = 0

    # Try all possible pairs of vertices
    for u in range(1, n):
        for v in range(u+1, n+1):
            path = find_path(u, v)
            path_set = set(path)

            max_components = max(max_components, count_components(path_set))

    return max_components

