                if size[a] > size[b] or (size[a] == size[b] and a > b):
                    path_a.append(a)
                    a = parent[a]
                else:
                    path_b.append(b)
                    b = parent[b]
            path_a.append(a)
            path = set(path_a + path_b[::-1])

            # Now, remove the path and count components >= K
            visited = [False]*(N+1)
            for node in path:
                visited[node] = True

            components = 0
            for node in range(1, N+1):
                if not visited[node]:
                    queue = deque()
                    queue.append(node)
                    visited[node] = True
                    cnt = 1
                    while queue:
                        current = queue.popleft()
                        for neighbor in adj[current]:
                            if not visited[neighbor]:
                                visited[neighbor] = True
                                queue.append(neighbor)
                                cnt += 1
                    if cnt >= K:
                        components += 1
            if components > max_components:
                max_components = components

    print(max_components)

solve()

#include <iostream>
#include <vector>
#include <set>
#include <algorithm>

using namespace std;

const int MAXN = 1e5 + 5;

vector<int> adj[MAXN];
int subtreeSize[MAXN];
int dp[MAXN][2];  // dp[x][0] => maximum K-components without x, dp[x][1] during optimal path

void dfsSubtreeSize(int u, int parent) {
    subtreeSize[u] = 1;
    for (int v : adj[u]) {
        if (v != parent) {
            dfsSubtreeSize(v, u);
            subtreeSize[u] += subtreeSize[v];
        }
    }
}

void dfsDP(int u, int parent) {
    int total = 0;
    int best1 = 0, best2 = 0; // Keeping track of two best results

    for (int v : adj[u]) {
        if (v != parent) {
            dfsDP(v, u);
            int result = dp[v][0];
            total += (subtreeSize[v] >= K); // Count if subtree size >= K

            if (result > best1) {
                best2 = best1;
                best1 = result;
            } else if (result > best2) {
                best2 = result;
            }
        }
    }

    dp[u][0] = total + (subtreeSize[u] >= K);
    dp[u][1] = total + best1 + best2;
}

int main() {
    int N, K;
    cin >> N >> K;

    for (int i = 0; i < N - 1; ++i) {
        int u, v;
        cin >> u >> v;
        adj[u].push_back(v);
        adj[v].push_back(u);
    }

    dfsSubtreeSize(1, -1);
    dfsDP(1, -1);

    int result = 0;
    for (int i = 1; i <= N; ++i) {
        result = max(result, dp[i][1]);
    }

    cout << result << endl;
    return 0;
}
