int main() {

	string s; cin >> s;

	//↑0→1↓2←3
	int direction = 0;
	bool isClockwise = true;
	int ans = 0;

	for (int i = 0; i < s.length(); i++) {
		char order = s[i];

		if (order == 'R') {
			direction++;
		}
		else {
			direction--;
		}

		if (direction == -4) {
			isClockwise = true;
			direction = 0;
		}

		if (direction == 4) {
			ans++;
			direction = 0;
		}
	}

	cout << ans << endl;

	return 0;
}

