signed main() {
	INIT;

	VAR(int, n);
	VEC(int, p, n);

	std::string s;

	V<int> P(n);
	REP(i, n) {
		--p[i];
		P[p[i]] = i;
	}

	int pos = 0;
	std::set<int> set;
	REP(i, n) {
		s += '(';
		set.insert(P[i]);
		while (set.count(pos)) { s += ')'; ++pos; }
	}

	std::stack<int> st;
	V<int> q(n, 0);
	int open = 0, close = 0;
	REP(i, s.size()) {
		if (s[i] == '(') {
			st.push(open);
			++open;
		}
		else {
			if (st.empty()) q[close] = 0;
			else { q[close] = st.top(); st.pop(); }
			++close;
		}
	}
	V<int> Q(n, -1);
	REP(i, n) {
		Q[q[i]] = i;
	}
	OUT(P == Q ? s : ":(")BR;

	return 0;
}

