    int sum = 0;
    rep(i,n){
        if(i==0){
            sum += a[0];
            ans = string(a[0],'(');
            ans.push_back(')');
        }else{
            if(sum >= a[i]){
                ans.push_back(')');
            }
            else{
                int d = a[i] - sum;
                ans += string(d,'(');
                sum += d;
                ans.push_back(')');
            }
        }
    }
    RSQ seg(2*n);
    for(int i=0;i<ans.size();i++){
        if(ans[i] == '('){
            seg.update(i,1);
        }else{
            seg.update(i,-1);
        }
    }

    unordered_map<int,int> op,cl;
    int cli = 0;
    int opi = 0;
    for(int i=0;i<ans.size();i++){
        if(ans[i] == '('){
            op[++opi]=i;
        }else{
            cl[++cli]=i;
        }
    }

    for(int i=0;i<n;i++){
        int si = op[a[i]];
        int ei = cl[i+1];
        int num = seg.query(si+1,ei);
        if(num != 0){
            cout << ":(" << endl;
            return 0;
        }
    }



    //debug(ans);
    int cnt = 0;
    for(int i=0;i<ans.size();i++){
        if(ans[i] == '('){
            cnt++;
        }else{
            cnt--;
            if(cnt < 0){
                cout << ":(" << endl;
                return 0;
            }
        }
    }
    if(cnt != 0){
        cout << ":(" << endl;
        return 0;
    }
    cout << ans << endl;
}

