bool solve( long n, const long* a, long offset )
{
    if(n<1) return true;

    long aa = a[0] - offset;
    if(aa<1 || aa>n) return false;

    ans.push_back(1);
    if (!solve( aa-1, &(a[1]), offset )) {
        return false;
    }
    ans.push_back(0);

    solve( n-aa, &(a[aa]), offset+aa );

    return true;
}

