string solve(const vector<int>& P) {
  string res = "";
  vector<int> stk;
  int sum = 0;
  for (int x : P) {
    while (sum < x) {
      res += '(';
      stk.push_back(++sum);
    }
    if (!stk.empty() && stk.back() == x) {
      stk.pop_back();
      res += ')';
    } else {
      return ":(";
    }
  }
  return res;
}

