  if(a->size() > b->size()) {
    for(auto e: *b) {
      a->push_back(e);
    }
    return a;
  } else {
    for(int i = (int) a->size() - 1; i >= 0; i--) {
      b->push_front((*a)[i]);
    }
    return b;
  }
}

