fn run() {
    input! {
        n: usize,
        p: [u32; n],
    }
    let mut ans = String::new();
    let mut stack = vec![];
    let mut now = 0;
    for p in p {
        while now < p {
            now += 1;
            stack.push(now);
            ans.push('(');
        }
        if stack.is_empty() || *stack.last().unwrap() != p {
            println!(":(");
            return;
        }
        ans.push(')');
        stack.pop();
    }
    println!("{}", ans);
}

fn main() {
    run();
}
