struct Point{
    ll x, y;
    Point(ll _x, ll _y){
        x = _x;
        y = _y;
    }
};

ll dist(Point a, Point b){
    ll ans = abs(a.x - b.x) + abs(a.y - b.y);
    return ans;
}

int main(){
    cin.tie(0);
    ios::sync_with_stdio(false);

    // input
    ll H, W;
    cin >> H >> W;

    vector<Point> V;
    FOR(i, 0, H){
        string s; cin >> s;
        FOR(j, 0, W){
            if(s[j]=='B'){
                V.push_back(Point(j, i));
            }
        }
    }

    ll L = V.size();
    ll ma = 0;
    FOR(i, 0, L){
        FOR(j, i+1, L){
            ll d = dist(V[i], V[j]);
            chmax(ma, d);
        }
    }
    p(ma);

    return 0;
}

