template <class T>
std::ostream &operator<<(std::ostream &s, const std::vector<T> &v) {
  if (v.empty())
    return s;
  s << *v.begin();
  for (auto iter = v.begin() + 1; iter != v.end(); ++iter)
    if (std::is_fundamental<T>::value)
      s << " " << *iter;
    else
      s << std::endl << *iter;
  return s;
}

int main() {
  // Input
  int H, W;
  std::cin >> H >> W;
  std::vector<std::vector<char>> c(H, std::vector<char>(W));
  for (int i = 0; i < H; ++i)
    for (int j = 0; j < W; ++j)
      std::cin >> c[i][j];

  int res = 0;
  for (int i = 0; i < H; ++i) {
    for (int j = 0; j < W; ++j) {
      if (c[i][j] == '.')
        continue;
      for (int k = 0; k < H; ++k) {
        for (int l = 0; l < W; ++l) {
          if (c[k][l] == '.')
            continue;
          int d = std::abs(k - i) + std::abs(l - j);
          chmax(res, d);
        }
      }
    }
  }

  // Output
  std::cout << res << std::endl;

  return 0;
}

