#define ABS(x)    ((x)>=0?(x):-(x))

int H, W;
char a[32][32];

int main()
{
	int r, c, r2, c2, dr, dc, ans;

	H = in(), W = in();
	for (r = 0; r < H; r++) ins(a[r]);
	ans = 0;
	for (r = 0; r < H; r++) for (c = 0; c < W; c++) if (a[r][c] == 'B') {
		for (r2 = H-1; r2 >= r; r2--) {
			dr = ABS(r2 - r);
			if (dr+W <= ans) goto next;
			for (c2 = W-1; c2 >= 0; c2--) if (a[r2][c2] == 'B') {
				dc = ABS(c2-c);
				if (dr + dc > ans) ans = dr + dc;
			}
		}
	next:;
	}
	printf("%d\n", ans);
	return 0;
}

