using static System.Math;
using System.Linq;
using System.Collections.Generic;
using System;

public class P
{
    public int x { get; set; }
    public int y { get; set; }
}

public class hello
{
    public static void Main()
    {
        string[] line = Console.ReadLine().Trim().Split(' ');
        var h = int.Parse(line[0]);
        var w = int.Parse(line[1]);
        var ps = new List<P>();
        for (int i = 0; i < h; i++)
        {
            var s = Console.ReadLine().Trim();
            for (int j = 0; j < w; j++)
                if (s[j] == 'B') ps.Add(new P { x = i, y = j });
        }
        getAns(ps);
    }
    static void getAns(List<P> ps)
    {
        var res = 0;
        var psc = ps.Count();
        for (int i = 0; i < psc - 1; i++)
            for (int j = i + 1; j < psc; j++)
                res = Max(res, Abs(ps[i].x - ps[j].x) + Abs(ps[i].y - ps[j].y));
        Console.WriteLine(res);
    }
}

