fn main() {
    let v = read_vec::<usize>();
    let h = v[0];
    let _w = v[1];

    let mut bs = Vec::new();
    for i in 0..h {
        let line = read::<String>();
        for (j, c) in line.chars().enumerate() {
            if c == 'B' {
                bs.push((i as i32, j as i32));
            }
        }
    }

    let blen = bs.len();
    let mut max = 0;
    for i in 0..blen - 1 {
        for j in i..blen {
            let dij = (bs[i].0 - bs[j].0).abs() + (bs[i].1 - bs[j].1).abs();
            max = std::cmp::max(max, dij);
        }
    }
    println!("{}", max);
}
