int main() {
    ll H, W;
    cin >> H >> W;
    V<PLL> lr(H);
    const PLL invalid(-1, -1);
    for(ll i = 0; i < H; i++) {
        string S;
        cin >> S;
        PLL p = invalid;
        for(ll j = 0; j < W; j++) if(S[j] == 'B') {
            p.first = j;
            break;
        }
        for(ll j = W - 1; 0 <= j; j--) if(S[j] == 'B') {
            p.second = j;
            break;
        }
        lr[i] = p;
    }

    ll ans = 0;
    for(ll i = 0; i < H; i++) {
        if(lr[i] == invalid) continue;
        ll hi = i, li, ri;
        tie(li, ri) = lr[i];
        for(ll j = i; j < H; j++) {
            if(lr[j] == invalid) continue;
            ll hj = j, lj, rj;
            tie(lj, rj) = lr[j];
            chmax(ans,
                  calc(hi, li, hj, rj),
                  calc(hi, ri, hj, lj));
        }
    }

    cout << ans << endl;
    return 0;
}

