      if (s[x] == 'B') ps.push_back(pt(x, y));
  }

  vpt chs;
  convex_hull(ps, chs);
  //printf("ps=%lu, chs=%lu\n", ps.size(), chs.size());

  int maxd = 0, n = chs.size();
  for (int i = 0; i < n; i++)
    for (int j = i + 1; j < n; j++) {
      int d = chs[i].md(chs[j]);
      if (maxd < d) maxd = d;
    }

  printf("%d\n", maxd);
  return 0;
}

