  i32 a = h + w;
  i32 b = 0;
  i32 c = h + w;
  i32 d = -(h + w);
  for (i32 i = 0; i < h * w; ++i) {
    if (s[i] != 'B') continue;
    i32 x = i / w;
    i32 y = i % w;
    a = MIN (a, x + y);
    b = MAX (b, x + y);
    c = MIN (c, x - y);
    d = MAX (d, x - y);
  }
  i32 ans = MAX (b - a, d - c);
  printf ("%" PRIi32 "\n", ans);
}

int main (void) {
  run();
  return 0;
}

