		int[] right = new int[H];
		Arrays.fill(left, -1);
		Arrays.fill(right, -1);
		for (int i = 0; i < H; i++) {
			char[] row = sc.next().toCharArray();
			for (int j = 0; j < W; j++) {
				if (row[j] == 'B') {
					if (left[i] == -1) left[i] = j;
					right[i] = j;
				}
			}
		}
		int ans = 0;
		for (int i = 0; i < H; i++) {
			if (left[i] == -1) continue;
			for (int j = i; j < H; j++) {
				if (left[j] == -1) continue;
				ans = Math.max(ans, j - i + Math.abs(left[i] - right[j]));
