public class hello
{
    public static int h, w;
    public static void Main()
    {
        string[] line = Console.ReadLine().Trim().Split(' ');
        h = int.Parse(line[0]);
        w = int.Parse(line[1]);
        var ss = new string[h];
        var used = new bool[h, w];
        for (int i = 0; i < h; i++) ss[i] = Console.ReadLine().Trim();
        var ps = getList(ss);
        getAns(ps);
    }
    static List<P> getList(string[] ss)
    {
        var res = new List<P>();
        var used = new bool[h, w];
        for (int i = 0; i < h; i++)
        {
            for (int j = 0; j < w; j++)
                if (ss[i][j] == 'B')
                    if (!used[i, j]) { used[i, j] = true; res.Add(new P { x = i, y = j }); break; }
            for (int j = w - 1; j >= 0; j--)
                if (ss[i][j] == 'B')
                    if (!used[i, j]) { used[i, j] = true; res.Add(new P { x = i, y = j }); break; }
        }
        for (int i = 0; i < w; i++)
        {
            for (int j = 0; j < h; j++)
                if (ss[j][i] == 'B')
                    if (!used[j, i]) { used[j, i] = true; res.Add(new P { x = j, y = i }); break; }
            for (int j = h - 1; j >= 0; j--)
                if (ss[j][i] == 'B')
                    if (!used[j, i]) { used[j, i] = true; res.Add(new P { x = j, y = i }); break; }
        }
        return res;
    }
    static void getAns(List<P> ps)
    {
        var res = 0;
        var psc = ps.Count();
        for (int i = 0; i < psc - 1; i++)
            for (int j = i + 1; j < psc; j++)
                res = Max(res, Abs(ps[i].x - ps[j].x) + Abs(ps[i].y - ps[j].y));
        Console.WriteLine(res);
    }
}

