    if n < 1000:
        ans = 1
        for x in s:
            for y in s:
                ans = max(ans,manh(x,y))
        print(ans)
    else:
        s.sort()
        l = tuple(s.pop(0))
        r = tuple(s.pop(-1))
        k = quickhull(l,r,s,[(l,0),(r,n)],0,n)
        ans = 1
        for x in k:
            for y in k:
                ans = max(ans,manh(x,y))
        print(ans)
    return

