// 拡張ユークリッド互除法
ll extgcd(ll a, ll b, ll &x, ll &y) {
    if (a < b) return extgcd(b, a, y, x);
    if (b) {
        ll g = extgcd(b, a%b, y, x);
        y -= a / b * x;
        return g;
    } else {
        x = 1, y = 0;
        return a;
    }
}

