template<class T>
T ext_gcd(T a, T b, T& x, T& y){
  T d = a;
  if(b == 0){
    x = 1;
    y = 0;
  }
  else{
    d = ext_gcd<T>(b, a%b, y, x);
    y -= (a / b) * x;
  }
  return d;
}

template<class T>
bool ext_gcd_n(T a, T b, LL c, T& x, T& y, bool zero=true) {
  auto g = ext_gcd(a, b, x, y);
  if(c % g != 0) return false;
  LL d = c / g;
  x *= d;
  y *= d;
  auto dx = b / g;
  auto dy = a / g;

  auto t = T{0};
  if(x < 0)
    t = -x / dx + 1;
  else
    t = -x / dx;

  x += t * dx;
  if(!zero && x == 0){
    ++t;
    x += dx;
  }

  y -= t * dy;
  return (y < 0 || (y == 0 && zero));
}

