int greedy(int a, int b, int x) {
	int cnt = 0;

	cnt += x / b; x %= b;
	cnt += x / a; x %= a;
	cnt += x;

	return cnt;
}
int opt(int a, int b, int x) {
	vector<int> dp(x + 1, inf);
	dp[0] = 0;
	rep(i, 0, x) {
		chmin(dp[i + 1], dp[i] + 1);
		if(i + a <= x) chmin(dp[i + a], dp[i] + 1);
		if (i + b <= x) chmin(dp[i + b], dp[i] + 1);
	}
	return dp[x];
}
