long long greedy(int a, int b, long long v, long long acc = 0) {
    if (v == 0) return acc;
    if (v / b) {
        return greedy(a, b, v % b, acc + v / b);
    } else if (v / a) {
        return greedy(a, b, v % a, acc + v / a);
    } else {
        return acc + v;
    }
}

