        cout << -1 << endl;
        return 0;
    }

    const ll inf = 5e15;
    ll ans = inf;
    for(ll b = 1; b <= A; b++) {
        ll orig = B * b;
        if(B * b % A == 0) continue;
        ll a = B * b / A + 1;
        ll mod = B * b % A;
        ll one = A - mod;
        orig += one;
        ll cnt_0 = b + one;
        ll cnt_1 = a;
        if(cnt_1 < cnt_0) chmin(ans, orig);
    }

