int64 calc(int64 a, int64 b, int64 x) {
    int64 greed = 0;
    greed = (x / b) + (x % b) / a + x % b % a;
    vector<int64> dp(x+1, INF);
    REP(i, x+1) dp[i] = i;
    REP(i, x+1) {
        if (i - a >= 0)
            chmin(dp[i], dp[i-a]+1);
    }
    REP(i, x+1) {
        if (i - b >= 0)
            chmin(dp[i], dp[i-b]+1);
    }
//    cout << a << " " << b << " " << x << ": " << greed << ", " << dp[x] << endl;
    return dp[x] - greed;
}

