    // Ax = By + k
    {
      Int k=(a-(b*y)%a)%a;
      Int x=(b*y+k)/a;
      if(x<y+k) chmin(ans,a*x);
    }
    // Ax + k = By
    if(y){
      Int k=(b*y)%a;
      Int x=(b*y-k)/a;
      if(x+k<y) chmin(ans,a*x);
    }
  }
