int main() {
  int L; string s; cin >> L >> s;

  vector<int> consWL(L+2), consBL(L+2);
  vector<int> consWR(L+2), consBR(L+2);
  vector<int> Bs, Ws;
  const int INF = 2 * L;
  Bs.emplace_back(-INF);
  Ws.emplace_back(-INF);
  for(int i=0; i<L; i++) {
    if(s[i] == 'W') consWL[i+1] = consWL[i] + 1;
    else consWL[i+1] = 0;

    if(s[i] == 'B') consBL[i+1] = consBL[i] + 1;
    else consBL[i+1] = 0;

    if(s[i] == 'B') Bs.emplace_back(i);
    if(s[i] == 'W') Ws.emplace_back(i);
  }
  for(int i=L-1; i>=0; i--) {
    if(s[i] == 'W') consWR[i+1] = consWR[i+2] + 1;
    else consWR[i+1] = 0;

    if(s[i] == 'B') consBR[i+1] = consBR[i+2] + 1;
    else consBR[i+1] = 0;
  }
  Bs.emplace_back(INF);
  Ws.emplace_back(INF);

  int ans = 0;
  for(int i=0; i<L; i++) {
    if(s[i] != '.') continue;
    int cand = 0;

    // self: i+1

    // left
    int cl = consBL[i];
    if(i - cl - 1 >= 0 and s[i - cl - 1] == 'W') cand = max(cand, cl);

    // right
    int cr = consBR[i+2];
    if(i + cr + 1 < L and s[i + cr + 1] == 'W') cand = max(cand, cr);

    // invalid?
    int wl = consWL[i];
    int wr = consWR[i+2];

    bool invalid = true;
    invalid &= (i - wl - 1 >= 0 and s[i - wl - 1] == 'B');
    invalid &= (i + wr + 1 < L and s[i + wr + 1] == 'B');
    if(invalid) continue;

    ans = max(ans, cand);
  }
  cout << ans << endl;
  return 0;
}

