int main(void){
    int L, ans = 0;
    string s;
    cin >> L >> s;
    for(int i=0;i<L;i++){
        if(s[i] != '.') continue;
        s[i] = 'W';
        int l = i, r = i;
        while(l > 0 && s[l] == 'W') l--;
        while(r < L-1 && s[r] == 'W') r++;
        if(s[l] == 'B' && s[r] == 'B'){
            s[i] = '.';
            continue;
        }
        int cnt = -1, sum = 0;
        for(int j=0;j<L;j++){
            if(s[j] == 'W'){
                sum += max(0, cnt);
                cnt = 0;
            }
            if(s[j] == 'B' && cnt >= 0) cnt++;
            if(s[j] == '.') cnt = -1;
        }
        s[i] = '.';
        ans = max(ans, sum);
    }
    cout << ans << endl;
    return 0;
}

