int L;

bool canput(int k, string &S){
	int i = k - 1, j = k + 1;
	while(i >= 0 && S[i] == 'W'){
		i--;
	}
	while(j < L && S[j] == 'W'){
		j++;
	}
	if(i < 0 || j >= L){
		return true;
	}
	if(S[i] == 'B' && S[j] == 'B'){
		return false;
	}
	else{
		return true;
	}
}

int put(int k, string &S){
	int ans = 0;
	int i = k - 1;
	while(i >= 0 && S[i] == 'B'){
		i--;
	}
	if(i >= 0 && S[i] == 'W'){
		ans = max(ans, k - i - 1);
	}
	int j = k + 1;
	while(j < L && S[j] == 'B'){
		j++;
	}
	if(j < L && S[j] == 'W'){
		ans = max(ans, j - k - 1);
	}
	return ans;
}

