using std::string;

int score(const string& s, char c) {
    auto litr = std::find(s.begin(), s.end(), c);
    if (litr == s.end()) return 0;

    int ret = 0;
    while (true) {
        auto ritr = std::find(litr + 1, s.end(), c);
        if (ritr == s.end()) break;
        if (std::find(litr, ritr, '.') == ritr) {
            ret += ritr - litr - 1;
        }
        litr = ritr;
    }
    return ret;
}

int main() {
    int n;
    string s;
    std::cin >> n >> s;

    int ans = 0;
    for (int i = 0; i < n; ++i) {
        if (s[i] != '.') continue;

        s[i] = 'W';
        int w = score(s, 'W'),
            b = score(s, 'B');
        if (b == 0) ans = std::max(ans, w);
        s[i] = '.';
    }

    std::cout << ans << std::endl;
    return 0;
}

