    if( s[i] == 'B' ) {
      int j = i;
      while( j < n && s[j] == 'B' ) {
	++j;
      }
      int len = j - i;
      if( s[i-1] == 'W' || ( j < n && s[j] == 'W' ) ) {
	if( s[i-1] == 'W' ) {
