#define MAX_N 3030303
long long extgcd(long long a, long long b, long long& x, long long& y) {
    long long d = a;
    if (b != 0) {
        d = extgcd(b, a % b, y, x);
        y -= (a / b) * x;
    } else {
        x = 1; y = 0;
    }
    return d;
}
long long mod_inverse(long long a, long long m) {
    long long x, y;
    if(extgcd(a, m, x, y) == 1) return (m + x % m) % m;
    else return -1;
}
vector<long long> fact(MAX_N+1, inf);
long long mod_fact(long long n, long long& e) {
    if(fact[0] == inf) {
        fact[0]=1;
        if(MAX_N != 0) fact[1]=1;
        for(ll i = 2; i <= MAX_N; ++i) {
            fact[i] = (fact[i-1] * i) % mod;
        }
    }
    e = 0;
    if(n == 0) return 1;
    long long res = mod_fact(n / mod, e);
    e += n / mod;
    if((n / mod) % 2 != 0) return (res * (mod - fact[n % mod])) % mod;
    return (res * fact[n % mod]) % mod;
}
// return nCk
long long mod_comb(long long n, long long k) {
    if(n < 0 || k < 0 || n < k) return 0;
    long long e1, e2, e3;
    long long a1 = mod_fact(n, e1), a2 = mod_fact(k, e2), a3 = mod_fact(n - k, e3);
    if(e1 > e2 + e3) return 0;
    return (a1 * mod_inverse((a2 * a3) % mod, mod)) % mod;
}

using mi = modint;

mi mod_pow(ll a, ll n) {
    mi ret = 1;
    mi tmp = a;
    while(n > 0) {
        if(n % 2) ret *= tmp;
        tmp = tmp * tmp;
        n /= 2;
    }
    return ret;
}

int l;
string s;

bool ok(int idx) {
    bool ret = true;
    if(s[idx] != '.') ret = false;
    bool tmp = true;
    if(idx == 0 || idx == l - 1) tmp = false;
    for(int i = idx - 1; i >= 0; i--) {
        if(s[i] == '.') {
            tmp = false;
            break;
        }
        if(s[i] == 'B') break;
    }
    for(int i = idx + 1; i < l; i++) {
        if(s[i] == '.') {
            tmp = false;
            break;
        }
        if(s[i] == 'B') break;
    }
    ret = ret & !tmp;
    return ret;
}

int cnt(int idx) {
    int ret = 0;
    int tmp = 0;
    for(int i = idx - 1; i >= 0; i--) {
        if(i && s[i] == 'B') tmp++;
        else if(s[i] == 'W') break;
        else {
            tmp = 0;
            break;
        }
    }
    ret += tmp;
    tmp = 0;
    for(int i = idx + 1; i < l; i++) {
        if(i != l - 1 && s[i] == 'B') tmp++;
        else if(s[i] == 'W') break;
        else {
            tmp = 0;
            break;
        }
    }
    ret += tmp;
    return ret;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);
    cin >> l >> s;
    int ans = 0;
    REP(i, l) {
        if(ok(i)) {
            // cout << i << endl;
            ans = max(ans, cnt(i));
        }
    }
    cout << ans << endl;
    return 0;
}

