using namespace std;

int n;
string s;

bool check(int x)
{
	if(s[x] != '.') return false;

	bool flag = false;
	for(int i = x+1; i < n; i++){
		if(s[i] == '.') return true;
		if(s[i] == 'B'){
			flag = true;
			break;
		}
	}
	bool flag2 = false;
	for(int i = x-1; i >= 0; i--){
		if(s[i] == '.') return true;
		if(s[i] == 'B'){
			flag2 = true;
			break;
		}
	}
	if(flag && flag2) return false;
	return true;
}

int calc(int x)
{
	s[x] = 'W';
	int ret = 0, cnt = 0; bool flag = false;
	for(int i = 0; i < s.size(); i++){
		if(s[i] == 'W'){
			if(flag){
				ret = max(ret, cnt);
			}
			flag = true;
			cnt = 0;
		}
		if(s[i] == '.') flag = false;
		if(s[i] == 'B'){
			if(flag) cnt++;
		}
	}
	s[x] = '.';
	return ret;
}

int main(void)
{
	cin >> n >> s;

	int ans = 0;
	for(int i = 0; i < n; i++){
		if(check(i)) ans = max(ans, calc(i));
	}
	cout << ans << endl;

	return 0;
}

