int solve(string s){
	int n=s.length();

	vector<int> w,b;
	for(int i=0;i<n;i++){
		if(s[i]=='W')w.pb(i);
		if(s[i]=='B')b.pb(i);

	}
	int wm=0;
	for(int i=1;i<w.size();i++){
		bool bo=1;
		for(int j=w[i-1]+1;j<w[i];j++){
			if(s[j]=='.')bo=0;
		}
		if(bo) wm=max(wm,w[i]-w[i-1]-1); 
	}
	int bm=0;
	for(int i=1;i<b.size();i++){
		bool bo=1;
		for(int j=b[i-1]+1;j<b[i];j++){
			if(s[j]=='.')bo=0;
		}
		if(bo) bm=max(bm,b[i]-b[i-1]-1); 
	}
	if(bm>0) return 0;
	return wm;
}

