int main()
{
	std::cin >> L >> S;

	int max{};
	for (int i{}; i < L; i++)
	{
		if (S[i] == 'B') continue;
		for (int j{i + 2}; j < L; j++)
		{
			if (S[j] == 'B' || S[j] == S[i]) continue;
			if (puttable(i, j))
				max = std::max(max, j - i - 1);
		}
	}

	printf("%d\n", max);

	return 0;
}

bool puttable(int left, int right)
{
	for (int i{left + 1}; i < right; i++)
		if (S[i] != 'B') return false;
	if (S[left] == '.')
	{
		int diff{left - 1};
		while (diff >= 0 && S[diff] == 'W') diff--;
		if (diff >= 0 && S[diff] == 'B') return false;
	}
	else
	{
		int diff{right + 1};
		while (diff < L && S[diff] == 'W') diff++;
		if (diff < L && S[diff] == 'B') return false;
	}
	return true;
}

