bool can_put(const int position, const std::string& str) {
	if (str[position] != '.') return false;
	int left = position - 1;
	while (left >= 0 && str[left] == 'W') --left;
	int right = position + 1;
	while (right < str.size() && str[right] == 'W') ++right;
	return  (left < 0 || str[left] == '.') || (right >= str.size() || str[right] == '.');
}
int count_remove_block(const int position, const std::string& str) {
	if (!can_put(position, str)) return 0;
	int left = position - 1;
	while (left >= 0 && str[left] == 'B') --left;
	int right = position + 1;
	while (right < str.size() && str[right] == 'B') ++right;
	if (left >= 0 && position - left != 1 && str[left] == 'W') return position - left - 1;
	if (right < str.size() && right - position != 1 && str[right] == 'W') return right - position - 1;
	return 0;
}
int main() {
	int n; std::cin >> n;
	std::string str; std::cin >> str;
	int max = 0;
	for (auto i = 0; i < n; ++i) {
		max = std::max(max, count_remove_block(i, str));
	}
	std::cout << max << std::endl;
}

