use std::cmp::max;

fn main() {
    let s = std::io::stdin();
    let mut sc = Scanner { stdin: s.lock() };
    let n: usize = sc.read();
    let mut s = sc.chars();

    let mut ans = 0;
    for pos in 0..n {
        if s[pos] != '.' {
            continue;
        }
        s[pos] = 'W';
        let w = capture(&s, 'W');
        let b = capture(&s, 'B');
        if b == 0 {
            ans = max(ans, w);
        }
        s[pos] = '.';
    }
    println!("{}", ans);
}

fn capture(s: &Vec<char>, color: char) -> usize {
    let n = s.len();
    let mut ans = 0;
    for left in 0..n {
        for right in (left + 1)..n {
            if s[left] != color || s[right] != color {
                continue;
            }
            if ((left + 1)..right).any(|i| s[i] == color || s[i] == '.') {
                continue;
            }
            let count = right - left - 1;
            ans = max(ans, count);
        }
    }
    ans
}

pub struct Scanner<R> {
    stdin: R,
}

impl<R: std::io::Read> Scanner<R> {
    pub fn read<T: std::str::FromStr>(&mut self) -> T {
        use std::io::Read;
        let buf = self
            .stdin
            .by_ref()
            .bytes()
            .map(|b| b.unwrap())
            .skip_while(|&b| b == b' ' || b == b'\n')
            .take_while(|&b| b != b' ' && b != b'\n')
            .collect::<Vec<_>>();
        unsafe { std::str::from_utf8_unchecked(&buf) }
            .parse()
            .ok()
            .expect("Parse error.")
    }
    pub fn vec<T: std::str::FromStr>(&mut self, n: usize) -> Vec<T> {
        (0..n).map(|_| self.read()).collect()
    }
    pub fn chars(&mut self) -> Vec<char> {
        self.read::<String>().chars().collect()
    }
}
