int Direct(int s){
	if((s>=65) && (s<=77)){
		return UP;
	}
	if((s>=78) && (s<=90)){
		return DOWN;
	}
	if((s>=97) && (s<=109)){
		return RIGHT;
	}
	return LEFT;
}

void calc(int N,char *S){
	int x=0,y=0;
	int d;

	for(int i=0;i<N;i++){
		d=Direct(S[i]);
		if(d==UP){
			y--;
		}
		if(d==DOWN){
			y++;
		}
		if(d==RIGHT){
			x++;
		}
		if(d==LEFT){
			x--;
		}
