using System;

public class hello
{
    public static void Main()
    {
        var n = int.Parse(Console.ReadLine().Trim());
        var s = Console.ReadLine().Trim();
        getAns(n, s);
    }
    static void getAns(int n, string s)
    {
        var w1 = 0;
        var w2 = 0;
        for (int i = 0; i < n; i++)
        {
            if (s[i] >= 'A' && s[i] <= 'M') w1++;
            else if (s[i] >= 'N' && s[i] <= 'Z') w1--;
            else if (s[i] >= 'a' && s[i] <= 'm') w2++;
            else w2--;
        }
        var ans = "";
        if (w1 > 0) ans += new string('A', w1);
        else if (w1 < 0) ans += new string('N', -w1);
        if (w2 > 0) ans += new string('a', w2);
        else if (w2 < 0) ans += new string('n', -w2);
        Console.WriteLine(ans.Length);
        Console.WriteLine(ans);
    }
}

