fn run() {
    input! {
        _n: usize,
        s: String,
    }
    let mut x = 0i32;
    let mut y = 0i32;
    for c in s.chars() {
        if 'A' <= c && c <= 'M' {
            x += 1;
        } else if 'N' <= c && c <= 'Z' {
            x -= 1;
        } else if 'a' <= c && c <= 'm' {
            y += 1;
        } else {
            y -= 1;
        }
    }
    let mut ans = String::new();
    while x > 0 {
        x -= 1;
        ans.push('A');
    }
    while x < 0 {
        x += 1;
        ans.push('Z');
    }
    while y > 0 {
        y -= 1;
        ans.push('a');
    }
    while y < 0 {
        y += 1;
        ans.push('z');
    }
    println!("{}\n{}", ans.len(), ans);
}

fn main() {
    run();
}
