    nx = (xp2 - xp1) / dist;
    ny = (yp2 - yp1) / dist;

    for (int i = 0; i < q; ++i) {
        cin >> xp >> yp;
        cout.precision(15);
        double t = nx * (xp - xp1) + ny * (yp - yp1);
        cout << xp1 + nx * t << ' ' << yp1 + ny * t << endl;
    }

    return 0;
}

