template<class t>
class straight_line{
	public:
		Vector2<t> f;
		Vector2<t> s;
		straight_line(){
			f = Vector2<t>(0,0);
			s = Vector2<t>(1,0);
		}
		straight_line(Vector2<t> a,Vector2<t> b){
			f = a;
			s = b;
		}

		Vector2<t> projection(Vector2<t> pos){
			pos -= f;
			Vector2<t> n = (s-f).normal();
			Vector2<t> res;
			res = n * dot(pos,n) + f;
			return res;
		}
};

