enum CCW{FRONT=1, LEFT=2, BACK=4, RIGHT=8, ON=16};
int ccw(P a, P b, P c)
{
	b -= a, c -= a;
	D s = sig(cross(b, c));
	if (s) return s>0 ? LEFT : RIGHT;
	if (sig(dot(b, c))<0) return BACK;
	if (sig(abs(c), abs(b))>0) return FRONT;
	return ON;
}

// 直線abと点cの距離
D distLP(P a, P b, P c)
{
	return abs(cross(b-a, c-a))/abs(b-a);
}

// 線分abと点cの距離
D distLSP(P a, P b, P c)
{
	if (sig(dot(b-a, c-a))<0) return abs(c-a);
	if (sig(dot(a-b, c-b))<0) return abs(c-b);
	return abs(cross(b-a, c-a))/abs(b-a);
}

