    }

    Point operator - (const Point &r) const {
        return Point(x - r.x, y - r.y);
    }

    // 叉积的模
    double operator ^ (const Point &r) const {
        return x * r.y - y * r.x;
    }

    // 点积
    double operator * (const Point &r) const {
        return x * r.x + y * r.y;
    }

    double length() {
        return hypot(x, y);
    }

};
Point operator * (const double &k, const Point &v) {
    return Point(v.x * k, v.y * k);
}
double distance(const Point &l, const Point &r) {
    return hypot(r.x - l.x, r.y - l.y);
}
typedef Point Vector;



// 直线或线段
struct Line {
    Point s, e;
    double len;
    Line() :len(INFINITY) {}
    Line(Point _s, Point _e) : s(_s), e(_e), len(distance(_s, _e)) {}
};

// 点向直线的投影点
Point projection(const Point &p, const Line &l) {
    Vector vec = (l.e - l.s) / l.len;
    return l.s + (p - l.s) * vec * vec;
}
